$.fn.highlight=function(){if($(this).length<1){throw"No Elements passed";return false}var d=$(this);var c=arguments[0]||{};var a=c.color||"#ffffff";var b=c.opacity||0.5;d.each(function(){if($(this).css("position")=="static"){$(this).css("position","relative")}$(this).children().each(function(){if($(this).css("position")=="static"){$(this).css("position","relative")}});var e=$("<div></div>").css({background:a,opacity:0,width:$(this).width(),height:$(this).height(),position:"absolute",top:0,left:0}).prependTo(this);$(this).hover(function(){e.stop(true).animate({opacity:b},100)},function(){e.stop(true).animate({opacity:0},400)})})};